(()=>{"use strict";(t=>{const e=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds");let s;document.getElementById("timer-days");const r=()=>{let t=(()=>{let t=(new Date("4 january 2023").getTime()-(new Date).getTime())/1e3;return{timeRemaining:t,hours:Math.floor(t/3600%24),minutes:Math.floor(t/60%60),seconds:Math.floor(t%60)}})();return e.textContent=1===t.hours.toString().length?"0"+t.hours:t.hours,n.textContent=1===t.minutes.toString().length?"0"+t.minutes:t.minutes,o.textContent=1===t.seconds.toString().length?"0"+t.seconds:t.seconds,t.timeRemaining};r()>0?s=setInterval(r,1e3):(clearInterval(s),e.textContent="00",n.textContent="00",o.textContent="00")})()})();